Bindings.Binding:
return function(key, keystate)
    if keystate == "DOWN" then
        MoveForwardStart();
    elseif keystate == "UP" then
        MoveForwardStop();
    end
end

		return function(key, keystate)
            if keystate == "DOWN" then
                MoveBackwardStart();
            elseif keystate == "UP" then
                MoveBackwardStop();
            end
        end

